import ReactDOM from 'react-dom';
import $ from 'jquery'; // Import jQuery
import 'jquery-modal'; // Import jQuery Modal plugin



import React, { useEffect } from 'react';

const ModalComponent = () => {
  useEffect(() => {
    const handleIFrameMessage = (e) => {
      if (typeof e.data === 'object') {
        return;
      }
      const args = e.data.split(":");
      let iframe;

      if (args.length > 2) {
        iframe = document.getElementById("JotFormIFrame-" + args[args.length - 1]);
      } else {
        iframe = document.getElementById("JotFormIFrame");
      }

      if (!iframe) {
        return;
      }

      switch (args[0]) {
        case "scrollIntoView":
          iframe.scrollIntoView();
          break;
        case "setHeight":
          iframe.style.height = args[1] + "px";
          break;
        case "collapseErrorPage":
          if (iframe.clientHeight > window.innerHeight) {
            iframe.style.height = window.innerHeight + "px";
          }
          break;
        case "reloadPage":
          window.location.reload();
          break;
        case "loadScript":
          const src = args[1];
          if (args.length > 3) {
            src = args[1] + ':' + args[2];
          }
          const script = document.createElement('script');
          script.src = src;
          script.type = 'text/javascript';
          document.body.appendChild(script);
          break;
        case "exitFullscreen":
          if (window.document.exitFullscreen) window.document.exitFullscreen();
          else if (window.document.mozCancelFullScreen) window.document.mozCancelFullScreen();
          else if (window.document.mozCancelFullscreen) window.document.mozCancelFullScreen();
          else if (window.document.webkitExitFullscreen) window.document.webkitExitFullscreen();
          else if (window.document.msExitFullscreen) window.document.msExitFullscreen();
          break;
        default:
          break;
      }

      const isJotForm = e.origin.indexOf("jotform") > -1 ? true : false;
      if (isJotForm && "contentWindow" in iframe && "postMessage" in iframe.contentWindow) {
        const urls = {
          docurl: encodeURIComponent(document.URL),
          referrer: encodeURIComponent(document.referrer),
        };
        iframe.contentWindow.postMessage(JSON.stringify({
          type: "urls",
          value: urls,
        }), "*");
      }
    };

    if (window.addEventListener) {
      window.addEventListener("message", handleIFrameMessage, false);
    } else if (window.attachEvent) {
      window.attachEvent("onmessage", handleIFrameMessage);
    }

    // Open modal using jQuery
    $('#ex1').modal();

    return () => {
      if (window.removeEventListener) {
        window.removeEventListener("message", handleIFrameMessage);
      } else if (window.detachEvent) {
        window.detachEvent("onmessage", handleIFrameMessage);
      }
    };
  }, []);

  return (
    <div id="ex1" className="modal">
      <h1>FREE at-home workouts and sports drills!</h1>
      <iframe
        id="JotFormIFrame-200934795587167"
        title="MSA Digital Sign Up - Popup"
        allowTransparency="true"
        allowFullScreen="true"
        allow="geolocation; microphone; camera"
        src="https://form.jotform.com/200934795587167"
        frameBorder="0"
        style={{
          minWidth: '100%',
          height: '600px',
          border: 'none'
        }}
        scrolling="no"
      />
      <script type="text/javascript">
        {`
          // Your additional JavaScript code (if needed)
        `}
      </script>
      <a href="#" rel="modal:close">Close</a>
    </div>
  );
};

export default ModalComponent;
