import React, { useEffect, useState } from 'react';
import './Slideshow.css'; // Make sure to adjust the CSS file path

const Slideshow = () => {
  const [slideIndex, setSlideIndex] = useState(0);

  useEffect(() => {
    // const handlePrevClick = () => {
    //   if (slideIndex > 0) {
    //     setSlideIndex(slideIndex - 1);
    //   }
    // };

    const handleNextClick = () => {
      if (slideIndex < 3) {
        setSlideIndex(slideIndex + 1);
      }
    };

    const interval = setInterval(handleNextClick, 3000); // Auto advance every 3 seconds

    return () => {
      clearInterval(interval);
    };
  }, [slideIndex]);

  <div className="slider">
      <div className="sliderWrapper" style={{ transform: `translateX(${-slideIndex * 100}%)` }}>
        <div className="sliderItem">
          <img src={f5} width="700" alt="house1" className="sliderImg" />
