        <div className="sliderItem">
          <img src={f6} width="700" alt="house2" className="sliderImg" />
        </div>
        <div className="sliderItem">
          <img src={f7} width="700" alt="house3" className="sliderImg" />
        </div>
        <div className="sliderItem">
          <img src={f8} width="700" alt="house4" className="sliderImg" />
        </div>
      </div>
      <button className="navButton" id="previous" onClick={() => setSlideIndex(slideIndex - 1)}>
        Previous
      </button>
      <button className="navButton" id="next" onClick={() => setSlideIndex(slideIndex + 1)}>
        Next
      </button>
    </div>
};

